// src/contract.js

import { ethers } from "ethers";
const { providers } = ethers; // Access providers from ethers


import contractABI from "./contractABI.json"; // Paste your ABI here or import it

const contractAddress = "0x262BA7b35C0863Db8f0ceFcB7d02592642906e20"; // Replace with your contract's address

// Function to initialize and connect to the smart contract
export const getContract = () => {
    if (typeof window.ethereum !== "undefined") {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        return new ethers.Contract(contractAddress, contractABI, signer);
    } else {
        console.error("MetaMask is not installed!");
        return null;
    }
};
